docker exec -e "CORE_PEER_ADDRESS=peer0.org2.example.com:7051" -e "CORE_PEER_LOCALMSPID=Org2MSP" -e "CORE_PEER_MSPCONFIGPATH=/opt/gopath/src/github.com/hyperledger/fabric/peer/crypto/peerOrganizations/org2.example.com/users/Admin@org2.example.com/msp" cli peer chaincode instantiate -o orderer.example.com:7050 -C mychannel -n cc_worker -l "$CC_RUNTIME_LANGUAGE" -v 1.0 -c '{"Args":[]}' -P "AND ('Org1MSP.member','Org2MSP.member')" 


